concs, _, g1s1, g2s1 = load(189, 1);
concs, _, g1s2, g2s2 = load(189, 2);
concs, _, g1s3, g2s3 = load(189, 3);

function figureS01(g1m, g2m, concs, ps)

    efcs = getODEparams(ps, concs)

    # ******* model simulations ********
    G1 = zeros(189, 8, 5)
    G2 = zeros(189, 8, 5)

    t = LinRange(0.0, 95.0, 189)
    for k = 1:5 # drug number
        for i = 1:8 # concentration number
            G1[:, i, k], G2[:, i, k], _ = predict(efcs[:, i, k], efcs[:, 1, k], t)
        end
    end

    p1 = DrugResponseModel.plot_fig1(concs[:, 1], hcat(G1[:, 1, 1], G1[:, 4:end, 1]), hcat(g1m[:, 1, 1], g1m[:, 4:end, 1]), "Dynamical Model Fits - Lapatinib", "G1", "A")
    p2 = DrugResponseModel.plot_fig1(concs[:, 1], hcat(G2[:, 1, 1], G2[:, 4:end, 1]), hcat(g2m[:, 1, 1], g2m[:, 4:end, 1]), "Dynamical Model Fits - Lapatinib", "S/G2", "B")
    p3 = DrugResponseModel.plot_fig1(concs[:, 2], hcat(G1[:, 1, 2], G1[:, 4:end, 2]), hcat(g1m[:, 1, 2], g1m[:, 4:end, 2]), "Dynamical Model Fits - Doxorubicin", "G1", "C")
    p4 = DrugResponseModel.plot_fig1(concs[:, 2], hcat(G2[:, 1, 2], G2[:, 4:end, 2]), hcat(g2m[:, 1, 2], g2m[:, 4:end, 2]), "Dynamical Model Fits - Doxorubicin", "S/G2", "D")
    p5 = DrugResponseModel.plot_fig1(concs[:, 3], hcat(G1[:, 1, 3], G1[:, 4:end, 3]), hcat(g1m[:, 1, 3], g1m[:, 4:end, 3]), "Dynamical Model Fits - Gemcitabine", "G1", "E")
    p6 = DrugResponseModel.plot_fig1(concs[:, 3], hcat(G2[:, 1, 3], G2[:, 4:end, 3]), hcat(g2m[:, 1, 3], g2m[:, 4:end, 3]), "Dynamical Model Fits - Gemcitabine", "S/G2", "F")
    p7 = DrugResponseModel.plot_fig1(concs[:, 4], hcat(G1[:, 1, 4], G1[:, 4:end, 4]), hcat(g1m[:, 1, 4], g1m[:, 4:end, 4]), "Dynamical Model Fits - Paclitaxel", "G1", "G")
    p8 = DrugResponseModel.plot_fig1(concs[:, 4], hcat(G2[:, 1, 4], G2[:, 4:end, 4]), hcat(g2m[:, 1, 4], g2m[:, 4:end, 4]), "Dynamical Model Fits - Paclitaxel", "S/G2", "H")
    p9 = DrugResponseModel.plot_fig1(concs[:, 5], hcat(G1[:, 1, 5], G1[:, 4:end, 5]), hcat(g1m[:, 1, 5], g1m[:, 4:end, 5]), "Dynamical Model Fits - Palbociclib", "G1", "I")
    p10 = DrugResponseModel.plot_fig1(concs[:,5], hcat(G2[:, 1, 5], G2[:, 4:end, 5]), hcat(g2m[:, 1, 5], g2m[:, 4:end, 5]), "Dynamical Model Fits - Palbociclib", "S/G2", "J")
    figureS1 = plot(p1, p2, p3, p4, p5, p6, p7, p8, p9, p10, size = (2400, 1050), layout = (2, 5))
    savefig(figureS1, "figureS01.svg")
end
